contracts/common/ControllableUpgradeable.content:
pragma solidity ^0.8.4;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract ControllableUpgradeable is OwnableUpgradeable {
    mapping(address => bool) public controllers;

    event ControllerChanged(address indexed controller, bool enabled);

    modifier onlyController() {
        require(controllers[msg.sender], "Controllable: Caller is not a controller");
        _;
    }

    function setController(address controller, bool enabled) public onlyOwner {
        controllers[controller] = enabled;
        emit ControllerChanged(controller, enabled);
    }

    function __Controllable_init() public onlyInitializing {
        controllers[msg.sender] = true;
        __Ownable_init();
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public override onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        assembly {
            sstore(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103, newOwner)
        }
        _transferOwnership(newOwner);
    }
}

contracts/common/Create2Factory.sol.content:
// Factory.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

contract Create2Factory is Initializable {
    event ContractCreated(address contractAddress);

    function initialize() public initializer {}

    function createContract(bytes32 salt, bytes memory bytecode) public returns (address) {
        address contractAddress;
        assembly {
            contractAddress := create2(0, add(bytecode, 0x20), mload(bytecode), salt)
            if iszero(extcodesize(contractAddress)) { revert(0, 0) }
        }
        emit ContractCreated(contractAddress);
        return contractAddress;
    }
}

contracts/EthMxcPriceAggregator.sol.content:
pragma solidity ^0.8.0;

import "./interfaces/AggregatorInterface.sol";
import "./common/ControllableUpgradeable.sol";
import {Proxied} from "hardhat-deploy/solc_0.8/proxy/Proxied.sol";
import {
UUPSUpgradeable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract EthMxcPriceAggregator is AggregatorInterface, ControllableUpgradeable {

    int public value;

    function initialize(int _vaule) external initializer {
        __Controllable_init();
        value = _vaule;
    }

    function setValue(int _vault) external onlyController {
        value = _vault;
    }

    function latestAnswer() public view returns (int256) {
        return int256(value);
    }

    function latestTimestamp() external view returns(uint256) {
        return block.timestamp;
    }

    function latestRound() external view returns (uint256) {
        return uint256(1);
    }

    function getAnswer(uint256 roundId) external view returns (int256) {
        return int256(value);
    }

    function getTimestamp(uint256 roundId) external view returns (uint256) {
        return block.timestamp;
    }
}

contract ProxiedEthMxcPriceAggregator is Proxied, UUPSUpgradeable, EthMxcPriceAggregator{
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

